--
-- Name: fixallseq(); Type: FUNCTION; Schema: public; Owner: everteamusr
--

CREATE FUNCTION fixallseq() RETURNS bigint
    LANGUAGE plpgsql
    AS $$DECLARE
   query text;
   maxval bigint;
   nbupdate integer;
   seq RECORD;
   tbl text;
   clm text;
BEGIN
   nbupdate:=0;
   FOR seq IN SELECT * FROM information_schema.columns WHERE column_default like 'nextval(%' LOOP
      tbl := seq.table_name;
      clm := seq.column_name;
      query := 'SELECT COALESCE( MAX( ' || clm || '), 0)+1 FROM  ' || quote_ident(tbl) ;
      EXECUTE query INTO maxval;
      query := 'SELECT SETVAL(pg_get_serial_sequence(' || quote_literal(tbl) || ',' || quote_literal(clm) || '), ' || maxval || ', false)';
      RAISE NOTICE 'Updating sequence for table % and field % with : %', tbl, clm, maxval;
      EXECUTE query INTO maxval;
      nbupdate := nbupdate +1;
   END LOOP;
   RETURN nbupdate;
END;$$;


